(1, 'فاتورة بيع بالتقسيط', 1, 'فاتورة بيع بالتقسيط (تستخدم لبيع المنتجات أو الخدمات مع جدول أقساط محدد، وتوليد سندات قبض/صرف مرتبطة بالأقساط، مع تحديث المخزون تلقائياً).', 'sales', 'installment_sale', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(2, 'فاتورة بيع', 1, 'فاتورة بيع للعميل (بيع منتجات أو خدمات للعميل النهائي، مع إمكانية الدفع نقداً أو بالتقسيط، وتوليد سندات قبض/صرف مرتبطة بالفاتورة، وتحديث المخزون تلقائياً).', 'sales', 'sale', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(3, 'فاتورة شراء', 1, 'فاتورة شراء من المورد (تستخدم لتسجيل مشتريات المنتجات أو الخدمات من الموردين، مع تحديث المخزون تلقائياً، وربط الفاتورة بطرق الدفع المختلفة).', 'purchases', 'purchase', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(4, 'مرتجع بيع', 1, 'مرتجع بيع للعميل (تستخدم لإرجاع منتجات تم بيعها سابقاً للعميل، مع تحديث المخزون وتوليد سندات مالية مرتبطة بالمرتجع).', 'sales', 'sale_return', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(5, 'مرتجع شراء', 1, 'مرتجع شراء من المورد (تستخدم لإرجاع منتجات تم شراؤها من المورد، مع تحديث المخزون وتوليد سندات مالية مرتبطة بالمرتجع).', 'purchases', 'purchase_return', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(6, 'عرض سعر', 1, 'عرض سعر للعميل (تستخدم لتقديم عرض أسعار للعميل قبل التحويل لفاتورة بيع، بدون تأثير على المخزون أو الحسابات المالية).', 'sales', 'quotation', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(7, 'طلب شراء', 1, 'طلب شراء من المورد (تستخدم لتقديم طلب شراء للمورد قبل التحويل لفاتورة شراء، بدون تأثير مباشر على المخزون).', 'purchases', 'purchase_order', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(8, 'طلب بيع', 1, 'طلب بيع للعميل (تستخدم لتسجيل طلبات العملاء قبل التحويل لفاتورة بيع، بدون تأثير مباشر على المخزون).', 'sales', 'sales_order', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(9, 'تسوية مخزون', 1, 'تسوية مخزون (تستخدم لتسوية الكميات الفعلية في المخزون مع النظام، مع تسجيل الفروقات في الأرباح أو الخسائر).', 'inventory', 'inventory_adjustment', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(10, 'تحويل مخزني', 1, 'تحويل مخزني بين المخازن (تستخدم لنقل المنتجات بين المخازن الداخلية، مع تحديث الكميات في كل مخزن).', 'inventory', 'stock_transfer', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(11, 'فاتورة خدمة', 1, 'فاتورة خدمة (تستخدم لبيع خدمات بدون منتجات ملموسة، مع إمكانية ربطها بسندات مالية).', 'services', 'service_invoice', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(12, 'إشعار دائن', 1, 'إشعار دائن (يستخدم لتقليل رصيد العميل أو المورد نتيجة خصم أو إرجاع أو تصحيح مالي).', 'finance', 'credit_note', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(13, 'إشعار مدين', 1, 'إشعار مدين (يستخدم لزيادة رصيد العميل أو المورد نتيجة إضافة مبلغ أو تصحيح مالي).', 'finance', 'debit_note', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(14, 'سند قبض', 1, 'سند قبض (يستخدم لتوثيق استلام مبالغ نقدية من العملاء أو جهات أخرى، ويرتبط غالباً بفواتير البيع أو المرتجعات).', 'finance', 'receipt', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(15, 'سند صرف', 1, 'سند صرف (يستخدم لتوثيق صرف مبالغ نقدية للموردين أو جهات أخرى، ويرتبط غالباً بفواتير الشراء أو المرتجعات).', 'finance', 'payment', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50'),
(16, 'فاتورة خصم', 1, 'فاتورة خصم (تستخدم لتسجيل خصومات أو عروض ترويجية على فواتير البيع، مع تأثير مباشر على الأرباح).', 'sales', 'discount_invoice', 1, 1, '2025-07-09 06:15:50', '2025-07-09 06:15:50');
