عرض تفاصيل فاتورة

if (!$authUser || !$companyId) {
return api_unauthorized('يتطلب المصادقة أو الارتباط بالشركة.');
}

$invoice = Invoice::with($this->relations)->findOrFail($id);

$canView = false;
// إضافة صلاحيات العرض
if ($authUser->hasPermissionTo(perm_key('admin.super'))) {
$canView = true;
} elseif ($authUser->hasAnyPermission([perm_key('invoices.view_all'), perm_key('admin.company')])) {
$canView = $invoice->belongsToCurrentCompany();
} elseif ($authUser->hasPermissionTo(perm_key('invoices.view_children'))) {
$canView = $invoice->belongsToCurrentCompany() && $invoice->createdByUserOrChildren();
} elseif ($authUser->hasPermissionTo(perm_key('invoices.view_self'))) {
$canView = $invoice->belongsToCurrentCompany() && $invoice->createdByCurrentUser();
}

if ($canView) {
return api_success(new InvoiceResource($invoice), 'تم جلب بيانات الفاتورة بنجاح');
}

return api_forbidden('ليس لديك صلاحية لعرض هذه الفاتورة.');
} catch (Throwable $e) {
return api_exception($e);
}
}

if (!$authUser || !$companyId) {
return api_unauthorized('يتطلب المصادقة أو الارتباط بالشركة.');
}

$installment = Installment::with($this->relations)->findOrFail($id);

// التحقق من صلاحيات العرض
$canView = false;
if ($authUser->hasPermissionTo(perm_key('admin.super'))) {
$canView = true; // المسؤول العام يرى أي قسط
} elseif ($authUser->hasAnyPermission([perm_key('installments.view_all'), perm_key('admin.company')])) {
// يرى إذا كان القسط ينتمي للشركة النشطة (بما في ذلك مديرو الشركة)
$canView = $installment->belongsToCurrentCompany();
} elseif ($authUser->hasPermissionTo(perm_key('installments.view_children'))) {
// يرى إذا كان القسط أنشأه هو أو أحد التابعين له وتابع للشركة النشطة
$canView = $installment->belongsToCurrentCompany() && $installment->createdByUserOrChildren();
} elseif ($authUser->hasPermissionTo(perm_key('installments.view_self'))) {
// يرى إذا كان القسط أنشأه هو وتابع للشركة النشطة
$canView = $installment->belongsToCurrentCompany() && $installment->createdByCurrentUser();
}

if ($canView) {
return api_success(new InstallmentResource($installment), 'تم استرداد القسط بنجاح.');
}

return api_forbidden('ليس لديك إذن لعرض هذا القسط.');
} catch (Throwable $e) {
return api_exception($e, 500);
}
}

عرض دفعة محددة

if (!$authUser || !$companyId) {
return api_unauthorized('يتطلب المصادقة أو الارتباط بالشركة.');
}

$payment = Payment::with($this->relations)->findOrFail($id);

$canView = false;
if ($authUser->hasPermissionTo(perm_key('admin.super'))) {
$canView = true;
} elseif ($authUser->hasAnyPermission([perm_key('payments.view_all'), perm_key('admin.company')])) {
$canView = $payment->belongsToCurrentCompany();
} elseif ($authUser->hasPermissionTo(perm_key('payments.view_children'))) {
$canView = $payment->belongsToCurrentCompany() && $payment->createdByUserOrChildren();
} elseif ($authUser->hasPermissionTo(perm_key('payments.view_self'))) {
$canView = $payment->belongsToCurrentCompany() && $payment->createdByCurrentUser();
}

if ($canView) {
return api_success(new PaymentResource($payment), 'تم استرداد الدفعة بنجاح.');
}

return api_forbidden('ليس لديك إذن لعرض هذه الدفعة.');
} catch (Throwable $e) {
return api_exception($e);
}
}
